const App = () => {
  const course = 'Half Stack application development'
  const parts = [{
name:'Fundamentals of React',
  exercises : 10
},

{
name:'Using props to pass data',
  exercises : 7
},

{
name:'State of a component',
  exercises : 14
}]

const Header = (props) =>{
	return(
<div>
<h1>{props.course}</h1>
</div>
)
}

const Part = (props) => {
	return(
<div>
{props.part} {props.exercises}
</div>
)
}

const Content = (props) => {
	return(
<div>
<Part part={props.parts[0]['name']} exercises={props.parts[0]['name']} />
<Part part={props.parts[1]['name']} exercises={props.parts[1][1]} />
<Part part={props.parts[2]['name']} exercises={props.parts[2][1]} />
</div>
)
}

  return (
    <div>
      <Header course={course} />
      <Content parts={parts}/>
      <p>Number of exercises {parts[0][1] + parts[1][1] + parts[2][1]}</p>
    </div>
  )
}

export default App
