const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
name:'Fundamentals of React',
  exercises1 : 10
}

const part2 = {
name:'Using props to pass data',
  exercises1 : 7
}

const part3 = {
name:'State of a component',
  exercises1 : 14
}

const Header = (props) =>{
	return(
<div>
<h1>{props.course}</h1>
</div>
)
}

const Part = (props) => {
	return(
<div>
{props.part} {props.exercises}
</div>
)
}

const Content = (props) => {
	return(
<div>
<Part part={props.part1[0]} exercises={props.part1[1]} />
<Part part={props.part2[0]} exercises={props.part2[1]} />
<Part part={props.part3[0]} exercises={props.part3[1]} />
</div>
)
}

  return (
    <div>
      <Header course={course} />
      <Content part1={part1} part2={part2} part3={part3}/>
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

export default App
